
var composite = require("../../../lib/express-composite");

exports.path = "/admin";

exports.index = function (req, res, next) {

    var map = {
            tmpl: "index",
            col: {
                module: "derp-admin",
                action: "login"
            }
        };

    composite.render(map, req, res);
};

exports.login = function (req, res, next) {
    res.render("login", {user: req.user, message: "error"});
};